let VALUE = Number(msg.payload.replace(/\D/g,''));
let TOGGLE = false

switch (true){

    case (/ttc/g.test(msg.payload)):
        context.TTC = VALUE;
        TOGGLE = true;
        break;

    case (/gms/g.test(msg.payload)):
        context.GMS = VALUE;
        TOGGLE = true;
        break;

    case (/atc/g.test(msg.payload)):
        context.ATC = VALUE;
        TOGGLE = true;
        break;

    case (/hvg/g.test(msg.payload)):
        context.HVG = VALUE;
        TOGGLE = true;
        break;

    default:
        context.CPM = VALUE || context.CPM;
        context.uSv = ((context.CPM / 544)).toFixed(4) || context.uSv;
}

if (TOGGLE == false){
    msg = [

        {payload: context.CPM, topic: "CPM"},
        {payload: context.uSv, topic: "uSv"},

        {payload: context.TTC, topic: "TTC"},
        {payload: context.GMS, topic: "GMS"},
        {payload: context.ATC, topic: "ATC"},
        {payload: context.HVG, topic: "HVG"},

    ];
}
else{
    msg = [

        {payload: undefined, topic: "CPM"},
        {payload: undefined, topic: "uSv"},

        {payload: context.TTC, topic: "TTC"},
        {payload: context.GMS, topic: "GMS"},
        {payload: context.ATC, topic: "ATC"},
        {payload: context.HVG, topic: "HVG"},

    ];
}
return msg;

